/*Table structure for table `volunteer` */

DROP TABLE IF EXISTS `volunteer`;

CREATE TABLE `volunteer` (
  `Id` int(11) NOT NULL AUTO_INCREMENT,
  `Number` varchar(50) DEFAULT NULL COMMENT '用户编号',
  `Type` varchar(50) DEFAULT NULL COMMENT '类型',
  `DepId` varchar(50) DEFAULT NULL COMMENT '社区编号，组织机构表中ID',
  `Address` varchar(500) DEFAULT NULL COMMENT '居住地址',
  `RealNameState` int(11) DEFAULT NULL COMMENT '实名制状态',
  `RealName` varchar(50) DEFAULT NULL COMMENT '真实姓名',
  `CardType` varchar(10) DEFAULT NULL COMMENT '证件类型',
  `CardNum` varchar(100) DEFAULT NULL COMMENT '证件编号',
  `UerName` varchar(50) DEFAULT NULL COMMENT '自定义名称',
  `Honor` int(11) DEFAULT NULL COMMENT '信誉指数',
  `Phone` varchar(50) DEFAULT NULL COMMENT '联系电话',
  `EMail` varchar(100) DEFAULT NULL COMMENT '邮件',
  `QQ` varchar(20) DEFAULT NULL COMMENT 'qq号码',
  `WeiXin` varchar(50) DEFAULT NULL COMMENT '微信号',
  `GroupID` int(11) DEFAULT NULL COMMENT '群体ID',
  `ThsScore` int(11) DEFAULT NULL COMMENT '感恩卡',
  `Score` int(11) DEFAULT NULL COMMENT '积分',
  `VID` varchar(50) DEFAULT NULL COMMENT '志愿者编号',
  `SocialNO` varchar(50) DEFAULT NULL COMMENT '社会组织编号',
  `State` int(11) DEFAULT '0' COMMENT '状态，审核状态',
  `Msg` text COMMENT '审核不通过原有',
  `PassWord` varchar(100) DEFAULT '000000' COMMENT '用户密码',
  `Doing` int(11) DEFAULT '1' COMMENT '0:不接受任务;1接受任务',
  `AddTime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `VolHeadImg` longblob,
  `Age` int(11) NOT NULL DEFAULT '0',
  `Sex` varchar(5) DEFAULT NULL,
  PRIMARY KEY (`Id`)
) ENGINE=InnoDB AUTO_INCREMENT=100083 DEFAULT CHARSET=utf8;

